mapper.update:
update linkis_stream_project_files set store_path = #{storePath}

    ,version = #{version}


    ,comment = #{comment}

where id = #{id}

mapper.select:
SELECT f.id,f.file_name,v.version,`store_path`,`store_type`,f.project_name,`create_time`,`create_by`,f.comment FROM linkis_stream_project_files f JOIN (SELECT MAX(VERSION) VERSION,file_name,project_name FROM linkis_stream_project_files GROUP BY file_name,project_name) v ON f.version= v.version AND f.file_name = v.file_name AND f.project_name = v.project_name AND f.project_name = #{projectName}
AND f.create_by = #{username}
AND f.file_name like concat('%', #{filename}, '%')

select `id`, `file_name`,`version`,`store_path`,`store_type`,`project_name`,`create_time`,`create_by` from
linkis_stream_project_files where project_name = #{projectName} and version = #{version} and file_name =
#{fileName}
