mapper.select:
select

from inlong_group

    is_deleted = 0

        and (
        creator = #{currentUser, jdbcType=VARCHAR} or find_in_set(#{currentUser, jdbcType=VARCHAR}, in_charges)
        )


        and (inlong_group_id like CONCAT('%',#{keyword},'%') or name like CONCAT('%',#{keyword},'%'))


        and inlong_group_id in

            #{inlongGroupId}



        and mq_type = #{mqType, jdbcType=VARCHAR}


        and status = #{status, jdbcType=INTEGER}


        and status in

            #{status}



        and inlong_cluster_tag in

            #{tag}





        order by ${orderField} ${orderType}


        order by create_time desc

select id, inlong_group_id, name, mq_type, mq_resource, inlong_cluster_tag, ext_params,
       in_charges, status, creator, modifier, create_time, modify_time
from inlong_group

    is_deleted = 0

        and mq_type = #{mqType, jdbcType=VARCHAR}


        and status = #{status, jdbcType=INTEGER}


        and status in

            #{status}



        and inlong_cluster_tag in

            #{tag}

select inlong_group_id
from inlong_group
where modify_time <= #{timeBefore, jdbcType=TIMESTAMP}
group by inlong_group_id
having min(is_deleted) > 0
limit #{limit, jdbcType=INTEGER}

mapper.update:
update inlong_group set inlong_group_id = #{inlongGroupId,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, mq_type = #{mqType,jdbcType=VARCHAR}, mq_resource = #{mqResource,jdbcType=VARCHAR}, daily_records = #{dailyRecords,jdbcType=INTEGER}, daily_storage = #{dailyStorage,jdbcType=INTEGER}, peak_records = #{peakRecords,jdbcType=INTEGER}, max_length = #{maxLength,jdbcType=INTEGER},
enable_zookeeper = #{enableZookeeper,jdbcType=INTEGER}, enable_create_resource = #{enableCreateResource,jdbcType=INTEGER}, lightweight = #{lightweight,jdbcType=INTEGER}, data_report_type = #{dataReportType,jdbcType=INTEGER}, inlong_cluster_tag = #{inlongClusterTag,jdbcType=VARCHAR},
ext_params = #{extParams,jdbcType=LONGVARCHAR}, in_charges = #{inCharges,jdbcType=VARCHAR}, followers = #{followers,jdbcType=VARCHAR}, previous_status = status, status = #{status,jdbcType=INTEGER}, is_deleted = #{isDeleted,jdbcType=INTEGER}, modifier = #{modifier,jdbcType=VARCHAR}, version = #{version,jdbcType=INTEGER} + 1 where id = #{id,jdbcType=INTEGER} and version = #{version,jdbcType=INTEGER}

update inlong_group
set previous_status = status,
    status          = #{status, jdbcType=INTEGER},
    modifier        = #{modifier, jdbcType=VARCHAR}
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and is_deleted = 0
